<?php

namespace Modularity\Module\Index;

class Index extends \Modularity\Module
{
    /**
     * Constructor
     */
    public function __construct()
    {
        // This will register the module
        $this->register(
            'index',
            __('Index', 'modularity'),
            __('Index', 'modularity'),
            __('Index card to use as an entry point for a "content section".', 'modularity'),
            array(),
            'data:image/svg+xml;base64,',
            null,
            3600*24*7,
            true
        );
    }
}
